import Inputs from "../inputs/Inputs";
import Card from "./Card";
import { books } from "../../helpers/cardsList";
import imageDefault from "./../../images/imageNotFound.png";
import ScrollToTopOnMount from "../../helpers/ScrollToTop";
impo


const CardList = () => {

  const savedUsername = localStorage.getItem('username');

if (savedUsername === null) {
  return <Navigate to="/sign-in" replace />;
}

    return ( 
      <main className="main">
        <ScrollToTopOnMount />
        <Inputs />        
        <section className="cards__catalog" id="cardsCatalog">
          {books.map((book) => {
            let imageURL;
            if (book.image) {
              imageURL = book.image;
            } else {
              imageURL = imageDefault;
            }

            return (

                <Card key={book.id}
                      id = {book.id}
                      title={book.title}
                      img ={imageURL}
                      author={book.author}
                      price={book.price}
                />

             )
          })}
        </section>
        </main>
     );
}

export default CardList;
