import { useEffect } from "react";

function ScrollToTopOnMount() {
    useEffect(() => {
      setTimeout(() => {
        window.scrollTo(0, 0);
      }, 500);
    }, []);

    return null;
  }

  export default ScrollToTopOnMount;
