import { useState } from 'react';

const useInput = () => {
  const [numberInput, setNumberInput] = useState(1);
  const [mainPrise, setMainPrise] = useState();
  const [calculatedPrice, setCalculatedPrice] = useState(0);

  const increase = () => {
    let enterValue = parseInt(numberInput) + 1;
    if (enterValue > 42) {
      enterValue = 42;
      alert("Sorry, but max quantity is 42");
    }
    setNumberInput(enterValue);
    setCalculatedPrice((numberInput * parseFloat(mainPrise)).toFixed(2));
  };

  const decrease = () => {
    let enterValue = parseInt(numberInput) - 1;
    if (enterValue <= 0) {
      alert("Sorry, but it is not possible to buy 0 or less books.");
      enterValue = 1;
    }
    setNumberInput(enterValue);
    setCalculatedPrice((numberInput * parseFloat(mainPrise)).toFixed(2));
  };

  const handleInput = (value) => {
    let handInput = value;

    if (handInput > 42) {
      handInput = 42;
      alert("Sorry, but max quantity is 42");
    } else if (handInput < 0) {
      handInput = 1;
      alert("Sorry, but the minimum quantity cannot be less than 1.");
    } else if (handInput === 0) {
      handInput = value.replace(/^0/, '');
    }

    setNumberInput(handInput);
    setCalculatedPrice((numberInput * parseFloat(mainPrise)).toFixed(2));
  };

  return [numberInput, setNumberInput, increase, decrease, handleInput, mainPrise, setMainPrise, calculatedPrice];
};

export default useInput;
