import Inputs from "../inputs/Inputs";
import Card from "./Card";
import { books } from "../../helpers/cardsList";
import imageDefault from "./../../images/imageNotFound.png";
import ScrollToTopOnMount from "../../helpers/ScrollToTop";
import { useStateб  } from "react";


const CardList = () => {
  const [displayedBooks, setDisplayedBooks] = useState(books);
  const [searchText, setSearchText] = useState('');
  const [selectedPrice, setSelectedPrice] = useState('all');

  const handleSearchChange = (event) => {
    setSearchText(event.target.value.toLowerCase());
  };

  const handlePriceChange = (event) => {
    setSelectedPrice(event.target.value);
  };

  useEffect(() => {
    let filteredBooks = books.filter((book) => {
      // Фільтр за назвою
      if (searchText && !book.title.toLowerCase().includes(searchText)) {
        return false;
      }

      // Фільтр за ціною
      if (selectedPrice !== 'all') {
        if (selectedPrice === 'low' && (book.price <= 0 || book.price > 15)) {
          return false;
        }
        if (selectedPrice === 'medium' && (book.price <= 15 || book.price > 30)) {
          return false;
        }
        if (selectedPrice === 'high' && (book.price <= 30)) {
          return false;
        }
      }

      return true;
    });

    setDisplayedBooks(filteredBooks);
  }, [searchText, selectedPrice]);

    return ( 
      <main className="main">
        <ScrollToTopOnMount />
        <Inputs handlePriceChange={handlePriceChange} handleSearchChange={handleSearchChange}/>        
        <section className="cards__catalog" id="cardsCatalog">
          {displayedBooks.map((book) => {
            let imageURL;
            if (book.image) {
              imageURL = book.image;
            } else {
              imageURL = imageDefault;
            }

            return (

                <Card key={book.id}
                      id = {book.id}
                      title={book.title}
                      img ={imageURL}
                      author={book.author}
                      price={book.price}
                />
            )
          })}
        </section>
        </main>
     );
}

export default CardList;
