import { useState } from "react";
import SinglePursheBook from "./SinglePursheBook";
import EmptyCart from "../../components/emptyCart/EmptyCart";
import img from "./../../images/imageNotFound.png";
import { AiFillDelete } from 'react-icons/ai';

const PurchaseList = () => {
    const [cart, setCart] = useState(
      JSON.parse(localStorage.getItem("cart")) || []
    );

    const totalPriceSum = cart.reduce(
      (acc, { totalPrice }) => acc + totalPrice,
      0
    );

    const handleDelete = (itemId) => {
      const updatedCart = cart.filter((item) => item.id !== itemId);
      setCart(updatedCart);
      localStorage.setItem("cart", JSON.stringify(updatedCart));
    };

    return (
      <main className="main">
        <div className="purchaseList">
          <button type="submit" className="purchase">
            Purchase
          </button>

          <span className="total__price__pur">
            Total price, $ {totalPriceSum.toFixed(2)}
          </span>

          <div className="list__items">
            {cart.length > 0 ? (
              cart.map((item) => (
                <div className="single__li" key={item.id}>
                  <SinglePursheBook
                    title={item.title}
                    img={item.image || img}
                    price={item.totalPrice}
                    count={item.quantity}
                    handleDelete={handleDelete}
                  />
                  <AiFillDelete
                    className="dlete__cart"
                    onClick={() => handleDelete(item.id)}
                  />
                </div>
              ))
            ) : (
              <EmptyCart />
            )}
          </div>
      </div>
    </main>
  );
};

export default PurchaseList;
