import avatar from "./../../images/avatarheader.png";
import style from './Header.module.css';
import { useState } from "react";
import { RiShoppingCart2Line } from "react-icons/ri";
import { Link } from "react-router-dom";
import {AiOutlineMenu, AiOutlineClose} from 'react-icons/ai';


const Header = () => {

    const [navigate, setNavigate] = useState(false);
    const {userName, setUserName} = useContext(ContextLogin);
    const savedUsername = localStorage.getItem('username');
    userName = savedUsername;

    return ( 
        <header className={style.header}>
        <div className={style.header__content}>

        <div className={style.leftheader}>
          <a href="https://prometheus.org.ua/prometheus-plus/front-end-ciklum/" ><span className={style.brandName}>X-course task / </span></a>
          <a href="https://www.facebook.com/irina.skaraeva" target="_blank" rel="noreferrer" className={style.author__header}><span> Iryna Skaraieva</span></a>
        </div>


        <nav className={(navigate && window.innerWidth < 700) ? [style.nav__navigate, style.active].join(' ') : [style.nav__navigate] }>
          <Link to='/cart'>
            <RiShoppingCart2Line color="gray" size={32} className={style.cart__header} onClick={() => setNavigate(false)}/>
          </Link>

          <Link to='/sign-in'>
            <button type="submit" className={style.btn} onClick={() => {
              localStorage.clear();
                setNavigate(false);
                }}><span>Sing-Out</span>
            </button>
          </Link>

          <a href="https://example.com"><img src={avatar} width="40px" height="40px" alt="circle-icon" className={style.headerimg2} onClick={() => setNavigate(false)}/></a>
          <a href="https://example.com" className={style.username} onClick={() => setNavigate(false)}><span>{savedUsername ? savedUsername : 'Username'}</span></a>
        </nav>

        <div className={style.main__nav}>
          <Link to='/cart'>
            <RiShoppingCart2Line color="gray" size={32} className={style.cart__header}/>
          </Link>

          <Link to='/sign-in'>
            <button type="submit" className={style.btn} onClick={() => {localStorage.clear(); setUserName(false);}}><span>Sing-Out</span></button>
          </Link>

          <a href="https://example.com"><img src={avatar} width="40px" height="40px" alt="circle-icon" className={style.headerimg2} /></a>
          <a href="https://example.com" className={style.username}><span>{savedUsername ? savedUsername : 'Username'}</span></a>
        </div>

          <div onClick={() => setNavigate(!navigate)} className={style.burger}>
            {navigate ? <AiOutlineClose size={32} color= 'white'/> : <AiOutlineMenu size={32} color= 'white'/>}
          </div>
        </div>
        </header>
     );
}

export default Header;
