import { useState } from "react";
import figureImg from "./../../images/avatar.png";
import './../../styles/style.css';



const SignIn = () => {

const [inputValue, setInputValue] = useState({
  input: '',
  user: 'Username',
  btnClass: ''

});

  const handleInputChange = (event) => {

    let inputIn = event.target.value;
    if ((inputIn.length > 4) || (inputIn.length < 16)) {

        setInputValue({
          input: inputIn,
          user: inputIn,
          btnClass: "",
        });

      } else {
        setInputValue({
          input: inputIn,
          user: inputIn,
          btnClass: "inactive",
        });

      }
  };

    return ( 
    <div className="main__content">
        <figure className="figure__sign">
          <img src={figureImg} alt="userphoto" className="figure__img" />
        </figure>

        <form action="/" method="post" className="form">

         <label htmlFor="name" className="label__for__name"> <span>{inputValue.user}</span></label>
         <input type="text" id="name" name="user_name" placeholder="type Username" className="input__for__name" onChange={handleInputChange} />

         <button type="submit" className={`btn__sign__in ${inputValue.btnClass}`} disabled={(inputValue.input).length < 4 || (inputValue.input).length > 16}>Sign-In</button>
        </form >
    </div>
    );
}

export default SignIn;
