import ScrollToTopOnMount from "../../helpers/ScrollToTop";
import { useState } from "react";


const CardList = () => {


    return ( 
      <main className="main">
        <ScrollToTopOnMount />
        <Inputs handlePriceChange={handlePriceChange} handleSearchChange={handleSearchChange}/>        
        <section className="cards__catalog" id="cardsCatalog">
          {displayedBooks.map((book) => {
            let imageURL;
            if (book.image) {
              imageURL = book.image;
            } else {
              imageURL = imageDefault;
            }

            return (

                <Card key={book.id}
                      id = {book.id}
                      title={book.title}
                      img ={imageURL}
                      author={book.author}
                      price={book.price}
                />
            )
          })}
        </section>
        </main>
     );
}

export default CardList;
