import { useState } from "react";
import figureImg from "./../../images/avatar.png";
import './../../styles/style.css';



const SignIn = (props) => {

const [inputValue, setInputValue] = useState('');

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
      };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (inputValue.length >= 4 && inputValue.length <= 16) {
      localStorage.setItem('username', inputValue);
      props.history.push('/book-list');
    }
  };


    return ( 
    <div className="main__content">
        <figure className="figure__sign">
          <img src={figureImg} alt="userphoto" className="figure__img" />
        </figure>

        <form action="/" method="post" className="form" onSubmit={handleSubmit} >

         <label htmlFor="name" className="label__for__name"> <span>Username</span></label>
         <input type="text" id="name" name="user_name" placeholder="type Username" className="input__for__name" onChange={handleInputChange} />

         <button type="submit" className={`btn__sign__in ${inputValue.length < 4 || inputValue.length > 16 ? 'inactive' : ''}`} disabled={inputValue.length < 4 || inputValue.length > 16}>Sign-In</button>
        </form >
    </div>
    );
}

export default SignIn;
