import searchIcon from "./../../images/search_icon.svg";
import arrowDown from "./../../images/arrow-down.png";
import { useState } from "react";

const Inputs = ({ handleSearchChange, handlePriceChange }) => {
  const [selectedPrice, setSelectedPrice] = useState('all');
  const [searchText, setSearchText] = useState('');

  const handleSelectChange = (event) => {
    setSelectedPrice(event.target.value);
    handlePriceChange(event.target.value, searchText);
  };

  const handleSearchTextChange = (event) => {
    const text = event.target.value;
    setSearchText(text);
    handleSearchChange(text, selectedPrice);
  };

  return ( 
    <section className="input__section">
      <div className="input">
        <input type="text" placeholder="Search by book name" className="search__input" value={searchText} onChange={handleSearchTextChange} />
        <img id="arrow_up" className="search__icon" src={searchIcon} alt="arrow-up" height="25px" />
      </div>

      <div className="price">
        <select value={selectedPrice} onChange={handleSelectChange} className='choose__price__input'>
          <option value="all">All Books</option>              
          <option value="low">Price: 0-15</option>
          <option value="medium">Price: 15-30</option>
          <option value="high">Price: 30+</option>
        </select>
      </div>
    </section>
  );
}

export default Inputs;
