import img from "./../../images/imageNotFound.png";
import { useState, useEffect } from "react";
import EmptyCart from "./../../components/emptyCart/EmptyCart";
import SinglePursheBook from "./SinglePursheBook";

const PurchaseList = () => {
  const [cart, setCart] = useState(JSON.parse(localStorage.getItem("cart")));

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart"));
    if (storedCart) {
      setCart(storedCart);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  return (
    <main className="main">
      <div className="purchaseList">
        <button type="submit" className="purchase">
          Purchase
        </button>

        <span className="total__price__pur">
          Total price, ${cart.reduce((acc, item) => acc + item.totalPrice, 0)}
        </span>

        <div className="list__items">
          {cart.length > 0 ? (
            cart.map((item) => (
              <SinglePursheBook
                key={item.title}
                count={item.quantity}
                title={item.title}
                img={item.image || img}
                price={item.price}
              />
            ))
          ) : (
            <EmptyCart />
          )}
        </div>
      </div>
    </main>
  );
};

export default PurchaseList;
