import Inputs from "../inputs/Inputs";
import Card from "./Card";
import { books } from "../../helpers/cardsList";
import imageDefault from "./../../images/imageNotFound.png";
import ScrollToTopOnMount from "../../helpers/ScrollToTop";
import { useState, useEffect } from "react";


const CardList = () => {
  const [displayedBooks, setDisplayedBooks] = useState(books);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedPrice, setSelectedPrice] = useState("all");

  useEffect(() => {
    let filteredBooks = books;
    if (selectedPrice !== "all") {
      if (selectedPrice === "low") {
        filteredBooks = books.filter(
          (book) => book.price > 0 && book.price <= 15
        );
      } else if (selectedPrice === "medium") {
        filteredBooks = books.filter(
          (book) => book.price > 15 && book.price <= 30
        );
      } else if (selectedPrice === "high") {
        filteredBooks = books.filter((book) => book.price > 30);
      }
    }

    if (searchTerm !== "") {
      filteredBooks = filteredBooks.filter((book) =>
        book.title.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    setDisplayedBooks(filteredBooks);
  }, [selectedPrice, searchTerm]);

  const handlePriceChange = (selectedPrice) => {
    setSelectedPrice(selectedPrice);
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  return (
    <main className="main">
      <ScrollToTopOnMount />
      <Inputs
        handlePriceChange={handlePriceChange}
        handleSearchChange={handleSearchChange}
      />
      <section className="cards__catalog" id="cardsCatalog">
        {displayedBooks.map((book) => {
          let imageURL;
          if (book.image) {
            imageURL = book.image;
          } else {
            imageURL = imageDefault;
          }

          return (
            <Card
              key={book.id}
              id={book.id}
              title={book.title}
              img={imageURL}
              author={book.author}
              price={book.price}
            />
          );
        })}
      </section>
    </main>
  );
};

export default CardList;
