import { useContext, useState } from "react";
import { Link } from "react-router-dom";
import figureImg from "./../../images/avatar.png";
import './../../styles/style.css';

import { ContextLogin } from "../../helpers/Context";

const SignIn = () => {

const [userName, setUserName] = useContext(ContextLogin);

const [inputValue, setInputValue] = useState('');

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
      };

  const handleSubmit = (event) => {
        event.preventDefault();
        if (inputValue.length >= 4 && inputValue.length <= 16) {
          localStorage.setItem('username', inputValue);
        }
      };

    return ( 
    <div className="main__content">
        <figure className="figure__sign">
          <img src={figureImg} alt="userphoto" className="figure__img" />
        </figure>

        <form action="/" method="post" className="form" onSubmit={handleSubmit}>

         <label htmlFor="name" className="label__for__name">{userName ? <h4>Username</h4> : <h4>Nothing</h4>}</label>
         <input type="text" id="name" name="user_name" placeholder="type Username" className="input__for__name" onChange={handleInputChange} />

        <Link to='/book-list'>
         <button type="submit" className={`btn__sign__in ${inputValue.length < 4 || inputValue.length > 16 ? 'inactive' : ''}`} disabled={inputValue.length < 4 || inputValue.length > 16} onClick={() => setUserName()}>Sign-In</button>
        </Link>
        </form >
    </div>
    );
}

export default SignIn;
