import avatar from "./../../images/avatarheader.png";
import { useState } from "react";
import { RiShoppingCart2Line } from "react-icons/ri";
import { Link } from "react-router-dom";
import {AiOutlineMenu, AiOutlineClose} from 'react-icons/ai';

const Header = () => {
    const [navigate, setNavigate] = useState(false);

    return ( 
        <header className="header">
        <div className="header__content">

        <div className="leftheader">
          <a href="https://prometheus.org.ua/prometheus-plus/front-end-ciklum/" ><span className="brandName">X-course task / </span></a>
          <a href="https://www.facebook.com/irina.skaraeva" target="_blank" rel="noreferrer" className="author__header"><span> Iryna Skaraieva</span></a>
        </div>



        <nav className={navigate && window.innerWidth > 700 ? 'nav__navigate, active' : 'nav__navigate'} >
          <Link to='/cart'>
            <RiShoppingCart2Line color="gray" size={32} className='cart__header'/>
          </Link>

          <Link to='/sign-in'>
            <button type="submit" className="btn"><span>Sing-Out</span></button>
          </Link>

          <a href="https://example.com"><img src={avatar} width="40px" height="40px" alt="circle-icon" className="headerimg2" /></a>
          <a href="https://example.com" className="username"><span>Username</span></a>
        </nav>

          <div onClick={() => setNavigate(!navigate)} className="burger">
            {navigate ? <AiOutlineClose size={32} color= 'white'/> : <AiOutlineMenu size={32} color= 'white'/>}
          </div>
        </div>
        </header>
     );
}

export default Header;
