import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

import "./styles/index.css";
import SignIn from "./components/signin/SignIn";
import SpecificBook from "./components/specific-book/SpecificBook";
import PurchaseList from "./components/purchaseList/PurchaseList";
import CardList from "./components/cards/CardList";
import ErrorPage from "./components/error/ErrorPage";
import Header from "./components/header/Header";
import Footer from "./components/footer/Footer";
import { CartItemCountProvider } from "./context/CartContext";
import { ContextLoginProvider, ContextLogin } from "./helpers/Context";
import { useContext, use } from "react";


function App() {

  const { userName, setUserName } = useContext(ContextLogin);

  useEffect ( () => {
    const savedUsername = localStorage.getItem('username') || 'Username';
    if (userName) {
      setUserName(savedUsername)
    }
}, [])

  return (
    <div className="App">
      <Router>
        <ContextLoginProvider>
          <CartItemCountProvider>
          <Header />

            <Routes>
              <Route path="error" element={<ErrorPage />} />
              <Route path="sign-in" element={<SignIn />} />
              { userName ? (
                <>
              <Route path="book-list" element={<CardList />} />
              <Route path="specific-book/:id" element={<SpecificBook />} />
              <Route path="cart" element={<PurchaseList />} />
              </>
              ) : (
                <Navigate to="/sign-in" />
              )}
            </Routes>

          <Footer />
          </CartItemCountProvider>
        </ContextLoginProvider>
      </Router>
    </div>
  );
}

export default App;
