import { useState } from "react";
import SinglePursheBook from "./SinglePursheBook";
import EmptyCart from "../../components/emptyCart/EmptyCart";
import img from "./../../images/imageNotFound.png";
import { AiFillDelete } from 'react-icons/ai';

const PurchaseList = () => {
  const [cart, setCart] = useState(
    JSON.parse(localStorage.getItem("cart")) || []
  );

  const cartItems = Object.values(cart);
  const totalPriceSum = cartItems.reduce((acc, {totalPrice}) => acc + totalPrice, 0);

  const handleDelete = (id) => {
    const newCart = cartItems.filter(el => el.id !== id);
    setCart(newCart);   
    localStorage.setItem("cart", JSON.stringify(newCart));
};
    const formattedTotalPrice = totalPriceSum.toFixed(2);
    localStorage.setItem("totalPriceSum", formattedTotalPrice);



  return (
    <main className="main">
      <div className="purchaseList">
        <button type="submit" className="purchase">
          Purchase
        </button>

        <span className="total__price__pur">
          Total price, $ {formattedTotalPrice}
        </span>

        <div className="list__items">
           {cartItems.length > 0 ? (
            cartItems.map((item) => (
                <div className="single__li" key={item.id} >
                    <SinglePursheBook
                        id={item.id}
                        title={item.title}
                        img={item.image || img}
                        price={item.price}
                        totalPrice={item.totalPrice}
                        count={item.quantity}
                        handleDelete={handleDelete}
                    />
                    <AiFillDelete className="dlete__cart" onClick={() => handleDelete(item.id)}/>
                </div>
            ))
          ) : (
            <>
              <EmptyCart />
              {localStorage.removeItem("cart")}
            </>
          )}
        </div>
      </div>
    </main>
  );
};

export default PurchaseList;
