import "./styles/index.css";

import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import SignIn from "./components/signin/SignIn";
import SpecificBook from "./components/specific-book/SpecificBook";
import EmptyCart from "./components/emptyCart/EmptyCart";
import PurchaseList from "./components/purchaseList/PurchaseList";
import CardList from "./components/cards/CardList";
import ErrorPage from "./components/error/ErrorPage";
import { ContextLogin } from "./helpers/Context";
import { useState } from "react";
import Header from "./components/header/Header";
import Footer from "./components/footer/Footer";
export const Context = React.createContext();


function App() {

  const [userName, setUserName] = useState(false);
  const [cartItemCount, setCartItemCount] = useState(0);
  return (
    <div className="App">

           {/**/}
      <Router>
          <ContextLogin.Provider value={{userName, setUserName}}>
            <Context.Provider></Context.Provider>
          <Header />

            <Routes>
              <Route path="error" element={<ErrorPage />} />
              <Route path="sign-in" element={<SignIn />} />
              <Route path="book-list" element={<CardList />} />
              <Route path="specific-book/:id" element={<SpecificBook />} />
              <Route path="cart" element={<PurchaseList />} />
            </Routes>

          <Footer />

          </ContextLogin.Provider>
       </Router>
    </div>
  );
}

export default App;
