import Inputs from "../inputs/Inputs";
import Card from "./Card";
import { books } from "../../helpers/cardsList";
import imageDefault from "./../../images/imageNotFound.png";
import ScrollToTopOnMount from "../../helpers/ScrollToTop";
import { useState } from "react";


const CardList = () => {
  const [displayedBooks, setDisplayedBooks] = useState(books);
  const [searchValue, setSearchValue] = useState('');
  const [selectedPrice, setSelectedPrice] = useState('all');

  const handlePriceChange = (selectedPrice) => {
    let filteredBooks;
    if (selectedPrice === 'low') {
      filteredBooks = books.filter((book) => book.price > 0 && book.price <= 15);
    } else if (selectedPrice === 'medium') {
      filteredBooks = books.filter((book) => book.price > 15 && book.price <= 30);
    } else if (selectedPrice === 'high') {
      filteredBooks = books.filter((book) => book.price > 30);
    } else {
      filteredBooks = books;
    }
    setDisplayedBooks(filteredBooks);
  };

  const handleNameChange = (searchValue) => {
    const filteredBooks = books.filter((book) => {
      return book.title.toLowerCase().includes(searchValue.toLowerCase());
    });
    setDisplayedBooks(filteredBooks);
    setSearchValue(searchValue);
  };

    return ( 
      <main className="main">
        <ScrollToTopOnMount />
        <Inputs handlePriceChange={handlePriceChange} handleNameChange={handleNameChange}/>        
        <section className="cards__catalog" id="cardsCatalog">
          {displayedBooks.map((book) => {
            let imageURL;
            if (book.image) {
              imageURL = book.image;
            } else {
              imageURL = imageDefault;
            }

            return (

                <Card key={book.id}
                      id = {book.id}
                      title={book.title}
                      img ={imageURL}
                      author={book.author}
                      price={book.price}
                />
            )
          })}
        </section>
        </main>
     );
}

export default CardList;
